export async function GetAll(id) {

    const resp = this.fetch(`${URL_API}/materias`, {
        method: "GET",
        headers: HEADERS()
    });

    const parsed = await resp.json()
    if (parsed.error) {
        return this.error(resp.status, parsed.error);
    }
    return parsed
}

