import { URL_API, HEADERS } from './conf'
import { Client } from './client'
import { Get as GetToken } from './secutiry'

export async function Get(id, token = null) {
    if (!token) token = (await GetToken()).token
    return await Client.send({ method: 'GET', path: `${URL_API}/materia/${id}`, token });
}

export async function GetAll(token = null) {
    if (!token) token = (await GetToken()).token
    return await Client.send({ method: 'GET', path: `${URL_API}/materias`, token });
}

export async function Create(data, token = null) {
    if (!token) token = (await GetToken()).token
    return await Client.send({ method: 'POST', path: `${URL_API}/materia`, data, token });
}

export async function Update(data, token = null) {
    if (!token) token = (await GetToken()).token
    return await Client.send({ method: 'PUT', path: `${URL_API}/materia/${data.id}`, data, token });
}

export async function Remove(id, token = null) {
    if (!token) token = (await GetToken()).token
    return await Client.send({ method: 'DELETE', path: `${URL_API}/materia/${id}`, token });
}